import React, { useState } from 'react';

const Sidebar = () => {
  const [collapsed, setCollapsed] = useState(false);

  return (
    <div className={`h-screen bg-blue-700 text-white transition-all duration-300 ${collapsed ? 'w-16' : 'w-56'} flex flex-col`}>
      <button
        className="absolute top-4 left-0 bg-blue-800 rounded-r-full p-2 focus:outline-none"
        onClick={() => setCollapsed(!collapsed)}
        aria-label="Toggle Sidebar"
      >
        <span className="text-lg">{collapsed ? '›' : '‹'}</span>
      </button>
      <div className="mt-16 flex flex-col gap-4 px-4">
        <span className="font-bold text-lg">{!collapsed && 'My Library'}</span>
        {/* Add navigation links here */}
      </div>
    </div>
  );
};

export default Sidebar;
