import React, { useState } from 'react';

const presetRules = [
  'Scientific information and WHO Guidelines',
  'Safety Information and Local Compliance',
  'Mechanism of Action and Guideline Compliance',
  'Formal Scientific Language and Technical Details',
  'First-Line Therapy Positioning',
  'Market-Specific Elements and Reporting Instructions',
  'Flexible Dosing and Consultation Availability',
  'Detailed Structure with Technical Specifications',
  'Targeted Benefits for Specific Groups (e.g., Travelers)',
];

const defaultPrompt = `Localize the provided English HTML text into German, ensuring the following: Adapt the tone to be polite and professional for a German-speaking audience, replacing informal phrases with formal equivalents. Incorporate cultural sensitivity by adjusting idiomatic expressions to resonate with German norms, avoiding direct translations that might feel unnatural. Ensure HTML tags remain intact, but translate any placeholder text within the tags (e.g., alt text for images, button labels). Use precise German terminology for technical or medical contexts, reflecting standard German vocabulary. Maintain the original structure and formatting of the HTML while ensuring the translated text fits naturally within the layout, adjusting sentence length if needed for readability. Provide a brief explanation of key changes to...`;

const LocalisationModal = ({ open, onClose, onLocalise }) => {
  const [customPrompt, setCustomPrompt] = useState(defaultPrompt);

  if (!open) return null;

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-30">
      <div className="bg-white rounded-2xl shadow-xl w-full max-w-4xl relative p-0">
        {/* Header */}
        <div className="flex items-center bg-blue-700 rounded-t-2xl px-6 py-3">
          <span className="text-white font-medium text-lg flex-1">Localisation Instructions</span>
          <button onClick={onClose} className="text-white text-xl ml-auto" aria-label="Close">&#10005;</button>
        </div>
        {/* Content */}
        <div className="flex p-8 pt-6 gap-8">
          {/* Preset Rules */}
          <div className="w-2/5">
            <div className="font-medium text-gray-700 mb-2 flex items-center gap-2">
              Preset Rules <span className="text-xs text-gray-400">&#128274;</span>
            </div>
            <ul className="text-gray-700 text-sm">
              {presetRules.map((rule, idx) => (
                <li key={rule} className="flex items-center py-1 border-b-2 last:border-b-0 border-gray-200">
                  <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={3} stroke="#1976d2" className="w-4 h-4 mr-2 flex-shrink-0">
                    <path strokeLinecap="round" strokeLinejoin="round" d="M9 5l7 7-7 7" />
                  </svg>
                  {rule}
                </li>
              ))}
            </ul>
          </div>
          {/* Custom Prompt */}
          <div className="w-3/5">
            <div className="font-medium text-gray-700 mb-2">Custom Prompt</div>
            <textarea
              className="w-full h-64 border border-gray-300 rounded-lg p-3 text-gray-700 resize-none focus:outline-none focus:ring-2 focus:ring-blue-300 hide-scrollbar"
              value={customPrompt}
              onChange={e => setCustomPrompt(e.target.value)}
              style={{ overflow: 'auto' }}
            />
          </div>
        </div>
        {/* Disclaimer (full width) */}
        <div className="px-8 pb-2 -mt-4">
          <div className="text-xs text-gray-400 bg-gray-50 rounded px-4 py-2 w-full">
            Disclaimer: The generated content will be closely tailored to the objectives defined during goal setting, aligning with the adoption cycle, target persona, and key messaging. Adhering to brand guidelines, the content will be crafted to ensure both relevance and accuracy. Please enter the keywords in the box below to create content specifically customized for your selected target persona
          </div>
        </div>
        {/* Action Buttons */}
        <div className="flex justify-end gap-2 px-8 pb-6">
          <button
            className="bg-gray-100 text-gray-700 rounded px-5 py-2 font-medium hover:bg-gray-200 transition"
            onClick={onClose}
          >
            Cancel
          </button>
          <button
            className="bg-blue-700 text-white rounded px-5 py-2 font-medium hover:bg-blue-800 transition"
            onClick={onLocalise}
          >
            Localise Content
          </button>
        </div>
      </div>
      {/* Custom CSS for hiding scrollbar */}
      <style>{`
        .hide-scrollbar::-webkit-scrollbar { display: none; }
        .hide-scrollbar { -ms-overflow-style: none; scrollbar-width: none; }
      `}</style>
    </div>
  );
};

export default LocalisationModal;
